import "./App.css";
import React from "react";
import SignUp from "./components/signup";
import SignIn from "./components/signin";
import Services from "./components/servicespage";
import { Route, Switch } from "react-router-dom";
import ForgotPassword1 from "./components/resetPassword/forgotpassword1";
import ForgotPassword2 from "./components/resetPassword/forgotpassword2";
import ForgotPassword3 from "./components/resetPassword/forgotpassword3";
import HomePage from "./components/homepage";
import ProductPage from "./components/productpage";
import Products from "./components/products";
import AboutUs from "./components/aboutus";
import Cart from "./components/cart";
import ProjectDetails from "./components/projectdetails";
import Success from "./components/payment/SuccessUI";
import ProjectPricing from "./components/projectprcing";

class App extends React.Component {
  state = {
    navhidden: true,
    loggedIn: false,
    user: {
      email: "",
      password: "",
      accessToken: "",
    },
  };
  setUser = (user_db) => {
    const email = user_db.email;
    const password = user_db.password;
    const accessToken = user_db.accessToken;
    const user = { email, password, accessToken };
    this.setState({ user });
  };
  setLogginStatus = () => {
    this.setState({ loggedIn: true });
  };
  handleSwitchNavState = () => {
    let navhidden = this.state.navhidden ? false : true;
    this.setState({ navhidden });
  };
  handleHideNav = () => {
    this.setState({ navhidden: true });
  };
  render() {
    return (
      <Switch>
        <Route path="/payment-success" render={() => <Success />} />
        <Route path="/cart" render={() => <Cart />} />
        <Route
          path="/product"
          render={(props) => (
            <ProductPage
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />

        <Route
          path="/products"
          render={(props) => (
            <Products
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              Products={this.state.user}
              {...props}
            />
          )}
        />

        <Route
          path="/about-us"
          render={(props) => (
            <AboutUs
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />
        <Route
          path="/sign-in"
          render={(props) => (
            <SignIn
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              setLogginStatus={this.setLogginStatus}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />
        <Route
          path="/sign-up"
          render={(props) => (
            <SignUp
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />
        <Route
          path="/forgot-password-1"
          render={(props) => (
            <ForgotPassword1
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />
        <Route
          path="/forgot-password-2"
          render={(props) => (
            <ForgotPassword2
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />
        <Route
          path="/forgot-password-3"
          render={(props) => (
            <ForgotPassword3
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />
        <Route
          path="/project-pricing"
          render={(props) => (
            <ProjectPricing
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />
        <Route
          path="/project-details"
          render={(props) => (
            <ProjectDetails
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />
        <Route
          path="/our-services"
          render={(props) => (
            <Services
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />
        <Route
          exact
          path="/"
          render={(props) => (
            <HomePage
              navHidden={this.state.navhidden}
              hideNav={this.handleHideNav}
              switchNavState={this.handleSwitchNavState}
              loggedIn={this.state.loggedIn}
              {...props}
            />
          )}
        />
      </Switch>
    );
  }
}

export default App;
