var Airtable = require("airtable");
var base = new Airtable({ apiKey: "keyXPG78PwXOJrDng" }).base(
  "appMPBJSSa0nNGRQO"
);

exports.getAllUsers = () => {
  let data = base("user")
    .select({
      view: "Grid view",
    })
    .eachPage(
      function page(records, fetchNextPage) {
        records.forEach(function (record) {
          return "Retrieved", record.get("email");
        });
        fetchNextPage();
      },
      function done(err) {
        if (err) {
          console.error(err);
          return;
        }
      }
    );
  console.log(data);
};
